package br.com.elvisassis.service;import br.com.elvisassis.exception.UnsupportedMathOperationException;import org.springframework.stereotype.Service;@Servicepublic class UtilService {  public boolean isNumeric(String strNumber) {    if (strNumber == null || strNumber.isEmpty()) return false;    String number = strNumber.replace( ",", ".");    return number.matches("[-+]?[0-9]*\\.?[0-9]+");  }  public double convertToDouble (String strNumber) {    if (strNumber == null || strNumber.isEmpty()) throw new UnsupportedMathOperationException("Please set a numeric value");    String number = strNumber.replace( ",", ".");    return Double.parseDouble(number);  }}