package br.com.elvisassis.controlleres;import br.com.elvisassis.model.Person;import br.com.elvisassis.service.PersonServices;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/person")public class PersonController {    public PersonController(PersonServices personServices) {        this.personServices = personServices;    }    private PersonServices personServices;  @GetMapping  public List<Person> findAll() {    return personServices.findAll();  }  @GetMapping("/{id}")  public Person findById(@PathVariable("id") String id) {    return personServices.findById(id);  }  @PostMapping  public Person create(@RequestBody Person person) {    return personServices.create(person);  }  @PutMapping  public Person update(@RequestBody Person person) {    return personServices.update(person);  }  @DeleteMapping("/{id}")  public void delete(@PathVariable("id") String id) {    personServices.delete(id);  }}