package br.com.elvisassis.mapper;import br.com.elvisassis.data.dto.v1.PersonDTO;import br.com.elvisassis.model.Person;import org.mapstruct.Mapper;import org.mapstruct.ReportingPolicy;import org.mapstruct.factory.Mappers;@Mapper(unmappedTargetPolicy = ReportingPolicy.IGNORE, componentModel = "spring")public interface PersonMapper {    public PersonMapper INSTANCE = Mappers.getMapper(PersonMapper.class);    PersonDTO personToPersonDto(Person person);    Person personDtoToPerson (PersonDTO personDto);}