package br.com.elvisassis.model;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.io.Serializable;import java.util.Objects;@AllArgsConstructor@NoArgsConstructor@Getter@Setter@Entity@Table(name = "person")public class Person implements Serializable {    private static final long sertialVersionUID =1;    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "first_name", nullable = false, length = 80)    private String firstName;    @Column(name = "last_name", nullable = false, length = 80)    private String lastName;    @Column(name = "address", nullable = false, length = 100)    private String address;    @Column(name = "gender", nullable = false, length = 6)    private String gender;    @Override    public boolean equals(Object o) {        if (!(o instanceof Person person)) return false;        return Objects.equals(getId(), person.getId()) && Objects.equals(getFirstName(), person.getFirstName()) && Objects.equals(getLastName(), person.getLastName()) && Objects.equals(getAddress(), person.getAddress()) && Objects.equals(getGender(), person.getGender());    }    @Override    public int hashCode() {        return Objects.hash(getId(), getFirstName(), getLastName(), getAddress(), getGender());    }}