package br.com.elvisassis.service;import br.com.elvisassis.model.Person;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import org.springframework.web.bind.annotation.RequestBody;import java.util.ArrayList;import java.util.List;import java.util.concurrent.atomic.AtomicLong;import java.util.logging.Logger;//@Slf4j@Servicepublic class PersonServices {    private final AtomicLong counter = new AtomicLong();    private Logger log = Logger.getLogger(PersonServices.class.getName());    public Person findById(String id) {        log.info("Finding one Person!");        Person person = new Person();        person.setId(counter.incrementAndGet());        person.setFirstName("Elvis");        person.setLastName("Assis");        person.setAddress("Rosario do Rio Grande - Minas Gerais - Brasil");        person.setGender("Male");        return person;    }    public List<Person> findAll() {        log.info("Finding all persons!");        List<Person> persons = new ArrayList<>();        for (int i = 0; i < 8; i++) {            Person person = mockPerson(i);            persons.add(person);        }        return persons;    }    public Person create(Person person) {        log.info("Creating one Person!");        return person;    }    public Person update(Person person) {        log.info("Updatind one Person!");        return person;    }    public void delete(String id) {        log.info("Deleting one Person!");    }    private Person mockPerson(int i) {        Person person = new Person();        person.setId(counter.incrementAndGet());        person.setFirstName("Fist name: " + i);        person.setLastName("Last name: " + i);        person.setAddress("Some Address in Brasil");        person.setGender("Gender");        return person;    }}