package br.com.elvisassis.controlleres;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@Slf4j@RestController@RequestMapping("/api/test/v1")public class TestLogController {  @GetMapping  public String testLog(){    log.trace("This is an TRACE log");    log.debug("This is an DEBUG log");    log.info("This is an INFO log");    log.warn("This is an WANR log");    log.error("This is an ERRO log");   return "Logs genereted successfuly!";  }}