package br.com.elvisassis.controllers;import br.com.elvisassis.data.dto.security.AccountCredentialsDTO;import br.com.elvisassis.services.AuthService;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.tags.Tag;import org.apache.commons.lang3.StringUtils;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@Tag(name = "Authentication Endpoint")@RestController@RequestMapping("/auth")public class AuthController {  public AuthController(AuthService service) {    this.service = service;  }  private AuthService service;  @Operation(summary = "Authenticates an user and returns a token")  @PostMapping("/signin")  public ResponseEntity<?> sigin(@RequestBody AccountCredentialsDTO credentials) {    if (credentialsIsInvalid(credentials)) return ResponseEntity.status(HttpStatus.FORBIDDEN).body("Invalid client request");    var token = service.sigIn(credentials);    if (token == null) return ResponseEntity.status(HttpStatus.FORBIDDEN).body("Invalid client request");    return ResponseEntity.ok().body(token);  }  private static boolean credentialsIsInvalid(AccountCredentialsDTO credentials) {    return credentials == null ||            StringUtils.isBlank(credentials.getPassword()) ||            StringUtils.isBlank(credentials.getPassword());  }}