package br.com.elvisassis.controllers;import br.com.elvisassis.controllers.docs.EmailControllerDocs;import br.com.elvisassis.data.dto.request.EmailRequestDTO;import br.com.elvisassis.services.EmailService;import io.swagger.v3.oas.annotations.tags.Tag;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import org.springframework.web.multipart.MultipartFile;@RestController@RequestMapping("/api/email/v1")public class EmailController implements EmailControllerDocs {    public EmailController(EmailService service) {        this.service = service;    }    private EmailService service;    @PostMapping    @Override    public ResponseEntity<String> sendEmail(@RequestBody EmailRequestDTO emailRequest) {        service.sendSimpleEmail(emailRequest);        return new ResponseEntity<>("e-Mail sent with success!",HttpStatus.OK);    }    @PostMapping("/withAttachment")    @Override    public ResponseEntity<String> sendEmailWithAttachment(            @RequestParam("emailRequest") String emailRequestJson,            @RequestParam("attachment") MultipartFile attachment) {        service.setEmailWithAttachment(emailRequestJson, attachment);        return new ResponseEntity<>("e-mail with attachment sent successfully", HttpStatus.OK);    }}