package br.com.elvisassis.data.dto;import br.com.elvisassis.mapper.PersonMapper;import br.com.elvisassis.model.Person;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import lombok.Data;import org.springframework.hateoas.RepresentationModel;@Data@JsonIgnoreProperties(ignoreUnknown = true)public class PersonDTO extends RepresentationModel<PersonDTO> {    private Long id;    private String firstName;    private String lastName;    private String address;    private String gender;    public Person toEntity() {        return PersonMapper.INSTANCE.personDtoToPerson(this);    }}