package br.com.elvisassis.data.dto;import br.com.elvisassis.mapper.PersonMapper;import br.com.elvisassis.model.Person;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import lombok.Data;import lombok.Getter;import org.springframework.hateoas.RepresentationModel;import org.springframework.hateoas.server.core.Relation;@Data@JsonIgnoreProperties(ignoreUnknown = true)@Relation(collectionRelation = "people")public class PersonDTO extends RepresentationModel<PersonDTO> {    private Long id;    private String firstName;    private String lastName;    private String address;    private String gender;    private Boolean enabled;    public String getName() {        return (firstName != null ? firstName : "") + (lastName != null ? lastName : "");    }    public Person toEntity() {        return PersonMapper.INSTANCE.personDtoToPerson(this);    }}