package br.com.elvisassis.model;import br.com.elvisassis.data.dto.BookDTO;import br.com.elvisassis.mapper.BookMapper;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.io.Serializable;import java.time.LocalDateTime;import java.util.Date;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "books")public class Book implements Serializable {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "author")    private String author;    @Column(name = "launch_date", nullable = false)    @Temporal(TemporalType.DATE)    private Date launchDate;    @Column(name = "price", nullable = false)    private Long price;    @Column(name = "title")    private String title;    public BookDTO toDto(){        return BookMapper.INSTANCE.bookToBookDTO(this);    }}