package br.com.elvisassis.services;import br.com.elvisassis.repository.UserRepository;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class UserService implements UserDetailsService {  public UserService(UserRepository repository) {    this.repository = repository;  }  UserRepository repository;  @Override  public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {      var user = repository.findByUserName(username);      if (user != null) return user;      else throw new UsernameNotFoundException("Username " + username + "not found");  }}