package br.com.elvisassis.config;import br.com.elvisassis.services.InstanceInformationService;import io.swagger.v3.oas.models.OpenAPI;import io.swagger.v3.oas.models.info.Info;import io.swagger.v3.oas.models.info.License;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class OpenApiConfig {    public OpenApiConfig(InstanceInformationService service) {        this.service = service;    }    InstanceInformationService service;    @Bean    OpenAPI customOpenApi() {        return new OpenAPI()            .info(new Info()                    .title("REST API's RESTFUL fom 0 with java, Spring Boot, Kubernets and Docker - V1 " + service.retrieveInstanceInfo())                    .version("V1")                    .description("REST API's RESTFUL fom 0 with java, Spring Boot, Kubernets and Docker")                    .termsOfService("https://linkedin.com/in/elvis-assis")                    .license(new License()                            .name("Apache 2")                            .url("https://linkedin.com/in/elvis-assis")                    )            );    }}