package br.com.elvisassis.controllers;import br.com.elvisassis.data.dto.BookDTO;import br.com.elvisassis.services.BookService;import io.swagger.v3.oas.annotations.tags.Tag;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Pageable;import org.springframework.data.domain.Sort;import org.springframework.hateoas.EntityModel;import org.springframework.hateoas.PagedModel;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import br.com.elvisassis.controllers.docs.BookControllerDocs;@RestController@RequestMapping("/api/book/v1")@Tag(name = "Book", description = "Endpoints for Managing Books")public class BookController implements BookControllerDocs {    public BookController(BookService service) {        this.service = service;    }    private BookService service;    @GetMapping(produces = {            MediaType.APPLICATION_JSON_VALUE,            MediaType.APPLICATION_XML_VALUE,            MediaType.APPLICATION_YAML_VALUE    })    @Override    public ResponseEntity<PagedModel<EntityModel<BookDTO>>> findAll(            @RequestParam(value = "page", defaultValue = "0") Integer page,            @RequestParam(value = "size", defaultValue = "12") Integer size,            @RequestParam(value = "direction", defaultValue = "asc") String direction    ) {        var sortDirection = "desc".equalsIgnoreCase(direction) ? Sort.Direction.DESC : Sort.Direction.ASC;        Pageable pageable = PageRequest.of(page, size, Sort.by(sortDirection, "author"));        var booksDTO = service.findAll(pageable);        return ResponseEntity.ok(booksDTO);    }    @GetMapping(value = "/{id}", produces = {            MediaType.APPLICATION_JSON_VALUE,            MediaType.APPLICATION_XML_VALUE,            MediaType.APPLICATION_YAML_VALUE    })    @Override    public ResponseEntity<BookDTO> findById(@PathVariable Long id) {        var dto = service.findById(id);        return ResponseEntity.ok(dto);    }    @PostMapping(produces = {            MediaType.APPLICATION_JSON_VALUE,            MediaType.APPLICATION_XML_VALUE,            MediaType.APPLICATION_YAML_VALUE    },            consumes = {                    MediaType.APPLICATION_JSON_VALUE,                    MediaType.APPLICATION_XML_VALUE,                    MediaType.APPLICATION_YAML_VALUE})    @Override    public ResponseEntity<BookDTO> create(@RequestBody BookDTO bookDTO) {        var dto = service.create(bookDTO);        return ResponseEntity.ok(dto);    }    @PutMapping(produces = {            MediaType.APPLICATION_JSON_VALUE,            MediaType.APPLICATION_XML_VALUE,            MediaType.APPLICATION_YAML_VALUE    },            consumes = {                    MediaType.APPLICATION_JSON_VALUE,                    MediaType.APPLICATION_XML_VALUE,                    MediaType.APPLICATION_YAML_VALUE            })    @Override    public ResponseEntity<BookDTO> update(@RequestBody BookDTO bookDTO) {        var dto = service.update(bookDTO);        return ResponseEntity.status(HttpStatus.CREATED).body(dto);    }    @DeleteMapping("/{id}")    @Override    public ResponseEntity<Void> delete(@PathVariable Long id) {        service.delete(id);        return ResponseEntity.noContent().build();    }}