package br.com.elvisassis.mapper;import br.com.elvisassis.data.dto.security.AccountCredentialsDTO;import br.com.elvisassis.model.User;public class UserMapper {    public static User accountCredentiaslDtoUser(AccountCredentialsDTO credentials, String passwordEncryty) {        User user = new User();        user.setUserName(credentials.getUsername());        user.setPassword(passwordEncryty);        user.setFullName(credentials.getFullname());        user.setAccountNonExpired(true);        user.setCredentialsNonExpired(true);        user.setAccountNonLocked(true);        user.setEnabled(true);        return user;    }    public static AccountCredentialsDTO userToAccountCredentialsDto(User user) {        AccountCredentialsDTO dto = new AccountCredentialsDTO();        dto.setUsername(user.getUsername());        dto.setFullname(user.getFullName());        dto.setPassword(user.getPassword());        return dto;    }}