package br.com.elvisassis.services;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Service;@Servicepublic class InstanceInformationService {    private static final String HOST_NAME = "HOST_NAME";    private static final String DEFAULT_ENV_INSTANCE_GUID = "LOCAL";    @Value("${" + HOST_NAME + ":" + DEFAULT_ENV_INSTANCE_GUID + "}")    private String hostname;    public String retrievInstanceInfo() {        return hostname.substring(hostname.length() - 5);    }}